import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import { useAuth } from '../context/AuthContext';

const CategoryArticlesPage = () => {
    const { category, subcategory } = useParams();
    const { user, logout } = useAuth();

    const [articles, setArticles] = useState([]);
    const [categoryInfo, setCategoryInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCategoryArticles = async () => {
            try {
                setLoading(true);

                // 'sales-marketing' -> 'sales_marketing'
                const facetValue = subcategory.replace(/-/g, '_');

                const response = await fetch(
                    `http://localhost:3003/api/articles/categories/${facetValue}`
                );

                if (!response.ok) {
                    if (response.status === 404) {
                        setError('Category not found');
                    } else {
                        setError('Failed to fetch articles');
                    }
                    return;
                }

                const data = await response.json();
                setCategoryInfo(data.category);
                setArticles(data.articles || []);
            } catch (err) {
                console.error('Error fetching category articles:', err);
                setError('Failed to fetch articles');
            } finally {
                setLoading(false);
            }
        };

        fetchCategoryArticles();
    }, [category, subcategory]);

    if (loading) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                        <p className="mt-4 text-gray-600">Loading articles...</p>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="text-red-500 text-xl font-medium">{error}</div>
                        <button
                            onClick={() => window.location.reload()}
                            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                        >
                            Retry
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white">
            <Header user={user} onLogoutClick={logout} />
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">
                        {categoryInfo?.label || subcategory.replace(/-/g, ' ')}
                    </h1>
                    <p className="text-gray-600">
                        {categoryInfo?.facet?.label ? `${categoryInfo.facet.label}: ` : ''}
                        Articles in this category
                    </p>
                </div>

                {articles.length === 0 ? (
                    <div className="text-center py-12">
                        <div className="text-gray-400 text-lg mb-4">
                            No articles found in this category
                        </div>
                        <p className="text-gray-500">
                            Check back later for new content.
                        </p>
                    </div>
                ) : (
                    <div className="grid gap-6 md:grid-cols-1">
                        {articles.map((article) => (
                            <div
                                key={article.id}
                                className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow"
                            >
                                <h2 className="text-xl font-semibold text-gray-900 mb-3">
                                    {article.title}
                                </h2>
                                <div className="flex items-center text-sm text-gray-500 mb-4">
                                    <span>
                                        By {article.author?.email || 'Unknown Author'}
                                    </span>
                                    <span className="mx-2">•</span>
                                    <span>
                                        {article.created_at
                                            ? new Date(article.created_at).toLocaleDateString()
                                            : ''}
                                    </span>
                                </div>
                                <div className="flex flex-wrap gap-2 mb-4">
                                    {article.facetAssignments?.map((assignment) => (
                                        <span
                                            key={assignment.id}
                                            className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                                        >
                                            {assignment.value?.label}
                                        </span>
                                    ))}
                                </div>
                                <button
                                    onClick={() => {
                                        alert(
                                            `Article "${article.title}" (ID: ${article.id}) is in this category. Full article view coming soon.`
                                        );
                                    }}
                                    className="text-blue-600 hover:text-blue-800 font-medium"
                                >
                                    Read more →
                                </button>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default CategoryArticlesPage;
