import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// --- Assets / Icons ---
// Simple SVG icons based on the descriptions
const ExploreIcon = () => (
    <svg className="w-8 h-8 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM10 7v3m0 0v3m0-3h3m-3 0H7" />
    </svg>
);

const DraftIcon = () => (
    <svg className="w-8 h-8 text-purple-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
    </svg>
);

const EditIcon = () => (
    <svg className="w-8 h-8 text-indigo-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
    </svg>
);

const PublishIcon = () => (
    <svg className="w-8 h-8 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
);

const AiSparkle = () => (
    <svg className="w-5 h-5 text-yellow-400 inline-block ml-1" fill="currentColor" viewBox="0 0 20 20">
        <path d="M10 1l2.5 6 6 2.5-6 2.5L10 18l-2.5-6-6-2.5 6-2.5L10 1z" />
    </svg>
);


export const HeroSection = ({ onGenerate, isGenerating }) => {
    const [prompt, setPrompt] = useState('');

    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && !isGenerating && prompt.trim()) {
            onGenerate(prompt);
        }
    };

    return (
        <div className="relative overflow-hidden bg-white mb-12">
            {/* Background enhancement can go here (gradients, patterns) */}
            <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[1000px] h-[500px] bg-gradient-to-b from-blue-50 to-white opacity-50 rounded-full blur-3xl -z-10 pointer-events-none"></div>

            <div className="text-center max-w-4xl mx-auto py-16 px-4 sm:px-6">
                <h1 className="text-5xl md:text-6xl font-extrabold text-gray-900 tracking-tight leading-tight mb-6">
                    Turn Insights into <br />
                    <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-indigo-600">Actionable Guides</span> with AI
                </h1>

                <p className="text-xl text-gray-600 mb-10 max-w-2xl mx-auto leading-relaxed">
                    Briefer.pro is a living library of practical instructions.
                    Co-create with AI to build a structured network of knowledge.
                </p>

                {/* Input Field / Hero CTA */}
                <div className="max-w-xl mx-auto relative group">
                    <div className="absolute -inset-1 bg-gradient-to-r from-blue-400 to-indigo-400 rounded-lg blur opacity-25 group-hover:opacity-40 transition duration-200"></div>
                    <div className="relative bg-white rounded-lg shadow-xl flex items-center p-2 border border-gray-100">
                        <input
                            type="text"
                            className="flex-grow p-4 text-lg outline-none text-gray-800 placeholder-gray-400"
                            placeholder="What do you want to explain today?"
                            value={prompt}
                            onChange={(e) => setPrompt(e.target.value)}
                            onKeyDown={handleKeyDown}
                            disabled={isGenerating}
                        />
                        <button
                            onClick={() => prompt.trim() && onGenerate(prompt)}
                            disabled={isGenerating || !prompt.trim()}
                            className="bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-md transition-all duration-200 flex-shrink-0"
                        >
                            {isGenerating ? (
                                <svg className="w-6 h-6 animate-spin" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
