import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import ThreeColumnLayout from './ThreeColumnLayout';
import LeftNavigation from './LeftNavigation';
import RightSidebar from './RightSidebar';
import { HeroSection, HookSection, FeaturesSection } from './HomeComponents'; // Import new components
import { useAuth } from '../context/AuthContext';
import { ArticleManager } from '../utils/ArticleManager';
import { buildDetailedPrompt } from '../data/filterOptions'; // Import this to maintain default filter logic

const HomePage = () => {
    const { user } = useAuth();
    const navigate = useNavigate();
    const [isGenerating, setIsGenerating] = useState(false);

    const handleGenerate = async (userInput) => {
        setIsGenerating(true);

        // Default filters as per "simple" requirement (Professional, Detailed)
        const defaultFilters = {
            style: 'professional',
            length: 'detailed'
        };

        // We can treat the user input as both the topic and the core of the prompt
        // Or we can try to separate them. For now, let's use userInput as the topic,
        // and append it to the detailed prompt logic.
        // Actually, ArticleGenerator passed (topic, detailedPrompt).
        // detailedPrompt was built from filters. 
        // Let's replicate that behavior.

        const detailedPrompt = buildDetailedPrompt(defaultFilters);
        // Note: buildDetailedPrompt currently just returns a string describing style/length.
        // It doesn't include the topic usually, the backend/API might combine them.
        // Let's check how the previous ArticleGenerator called it: 
        // onGenerate(topic, detailedPrompt);
        // And the API call used:
        // body: JSON.stringify({ topic: topic, detailedPrompt: detailedPrompt })

        try {
            const response = await fetch('http://localhost:3003/api/ai/generate-article', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    topic: userInput,
                    detailedPrompt: detailedPrompt
                })
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.details || errorData.error || `API request failed: ${response.status}`;
                throw new Error(errorMessage);
            }

            const data = await response.json();
            const aiResponse = data.content;

            if (aiResponse.startsWith('ERROR:')) {
                alert(aiResponse);
                setIsGenerating(false);
                return;
            }

            // --- HTML Parsing Logic (Kept same as before) ---
            let title = userInput;
            let bodyContent = aiResponse;

            const h1Match = aiResponse.match(/<h1[^>]*>(.*?)<\/h1>/i);
            if (h1Match) {
                title = h1Match[1].trim();
                bodyContent = aiResponse.replace(h1Match[0], '').trim();
            }

            const parts = bodyContent.split(/<h2[^>]*>(.*?)<\/h2>/i);
            const newCards = [];

            if (parts[0] && parts[0].trim()) {
                const sectionId = `section-intro-${Date.now()}`;
                let htmlContent = parts[0].trim();
                if (!htmlContent.includes('<h2')) {
                    htmlContent = `<h2 class="text-2xl font-semibold mb-4 text-gray-800">Introduction</h2>` + htmlContent;
                }
                newCards.push({ id: sectionId, content: htmlContent });
            }

            for (let i = 1; i < parts.length; i += 2) {
                const sectionTitle = parts[i].trim();
                let sectionContent = parts[i + 1] ? parts[i + 1].trim() : '';
                if (!sectionContent && !sectionTitle) continue;
                const sectionId = `section-${i}-${Date.now()}`;
                let htmlContent = `<h2 class="text-2xl font-semibold mb-4 text-gray-800">${sectionTitle}</h2>` + sectionContent;
                newCards.push({ id: sectionId, content: htmlContent });
            }

            // Create and Save Article
            const activeId = ArticleManager.createArticle(title);
            ArticleManager.saveArticle(activeId, {
                title: title,
                cards: newCards
            });

            // Navigate to editor with fully formed article
            navigate('/create', {
                state: {
                    editArticle: {
                        id: activeId,
                        title: title,
                        cards: newCards
                    }
                }
            });

        } catch (error) {
            console.error('Generation Error:', error);
            alert(`Generation failed: ${error.message}`);
        } finally {
            setIsGenerating(false);
        }
    };

    return (
        <div className="min-h-screen bg-white flex flex-col">
            <Header user={user} onLoginClick={() => { }} onNavigate={() => { }} />

            <ThreeColumnLayout
                left={<LeftNavigation />}
                right={<RightSidebar />}
            >
                {/* Main Content Area */}
                <div className="w-full">

                    <HeroSection
                        onGenerate={handleGenerate}
                        isGenerating={isGenerating}
                    />

                    <div className="px-4 sm:px-6 lg:px-8 max-w-7xl mx-auto">
                        <HookSection />
                        <FeaturesSection />
                    </div>

                </div>
            </ThreeColumnLayout>

            <Footer />
        </div>
    );
};

export default HomePage;
