import React, { useState } from 'react';

const ArticleGenerator = ({ onGenerate }) => {
  const [topic, setTopic] = useState('');
  const [detailedPrompt, setDetailedPrompt] = useState('');

  const handleGenerate = () => {
    if (topic.trim()) {
      onGenerate(topic, detailedPrompt);
    }
  };

  return (
    <div className="bg-gray-100 flex justify-center py-10 px-4 sm:px-6 lg:px-8 min-h-screen">
      <div className="container bg-white p-6 sm:p-8 rounded-lg shadow-lg max-w-3xl w-full">
        <h1 className="text-4xl font-bold mb-8 text-gray-900">Article Generator</h1>
        
        <div className="mb-8 p-6 border-2 border-gray-200 rounded-xl hover:border-indigo-500 transition-colors duration-300 bg-white shadow-sm">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">How To...</h2>
          <input
            type="text"
            value={topic}
            onChange={(e) => setTopic(e.target.value)}
            placeholder="write what you need"
            className="w-full p-4 text-lg border-2 border-gray-200 rounded-lg focus:outline-none focus:border-indigo-500 transition-colors bg-gray-50 placeholder-gray-400"
          />
        </div>

        <div className="mb-8">
          <label className="block text-xl font-semibold mb-4 text-gray-800">Detailed Prompt</label>
          <textarea
            value={detailedPrompt}
            onChange={(e) => setDetailedPrompt(e.target.value)}
            placeholder="Enter detailed instructions, context, or specific requirements..."
            className="w-full p-4 text-base border-2 border-gray-200 rounded-lg focus:outline-none focus:border-indigo-500 transition-colors bg-gray-50 min-h-[150px] resize-y"
          />
        </div>

        <div className="flex justify-end">
          <button
            onClick={handleGenerate}
            disabled={!topic.trim()}
            className={`px-8 py-3 rounded-lg text-white font-semibold text-lg transition-all transform hover:-translate-y-1 ${
              topic.trim() 
                ? 'bg-indigo-600 hover:bg-indigo-700 shadow-lg hover:shadow-xl' 
                : 'bg-gray-400 cursor-not-allowed'
            }`}
          >
            Generate
          </button>
        </div>
      </div>
    </div>
  );
};

export default ArticleGenerator;
