import React, { useState, useEffect } from 'react';
import Card from './components/Card';
import Header from './components/Header';
import CardEditorModal from './components/CardEditorModal';
import './App.css';

const App = () => {
  const [cards, setCards] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentCard, setCurrentCard] = useState(null);
  const [isNewCard, setIsNewCard] = useState(false);

  // Load saved data on component mount
  useEffect(() => {
    const savedData = localStorage.getItem('cardEditorData');
    if (savedData) {
      try {
        const parsedData = JSON.parse(savedData);
        setCards(parsedData);
      } catch (error) {
        console.error('Error loading saved data:', error);
      }
    } else {
      // Initialize with default content if no saved data
      initializeDefaultContent();
    }
  }, []);

  const initializeDefaultContent = () => {
    const defaultCards = [
      {
        id: 'intro',
        title: 'Introduction',
        content: '<p>Welcome to our revolutionary card-based editing system! This innovative approach allows you to edit text content in an intuitive, modular way. Each paragraph or section of your article becomes an editable card that you can modify independently.</p>',
        delta: null
      },
      {
        id: 'main-content',
        title: 'How the Card System Works',
        content: '<p>Each text block in this article is contained within a card. When you click the EDIT link, you\'ll see a modal dialog where you can modify the text content using our advanced editor.</p><p>The editor includes AI assistance features - simply type your prompt in the dedicated field and use the up arrow (↑) button to send it for processing. This helps you improve, expand, or refine your content with intelligent suggestions.</p>',
        delta: null
      },
      {
        id: 'features',
        title: 'Key Features',
        content: '<p><strong>Intuitive Editing:</strong> Click the edit button to open a modal with advanced text editing capabilities.</p><p><strong>AI Integration:</strong> Use prompts to enhance your content with AI assistance.</p><p><strong>Save Options:</strong> Choose between saving edits temporarily or saving to your profile permanently.</p><p><strong>Responsive Design:</strong> Works seamlessly on both desktop and mobile devices.</p><p><strong>Keyboard Shortcuts:</strong> Use Ctrl+Enter to save or Esc to cancel in the editor.</p>',
        delta: null
      },
      {
        id: 'conclusion',
        title: 'Conclusion',
        content: '<p>This card-based editing system represents the future of content creation and editing. By breaking down articles into manageable, editable cards, we make the writing and editing process more intuitive and efficient. Try editing any of these cards to experience the system firsthand!</p>',
        delta: null
      }
    ];
    setCards(defaultCards);
  };

  const saveAllData = () => {
    localStorage.setItem('cardEditorData', JSON.stringify(cards));
    localStorage.setItem('cardEditorTimestamp', new Date().toISOString());
  };

  const handleEditCard = (card) => {
    setCurrentCard(card);
    setIsNewCard(false);
    setIsModalOpen(true);
  };

  const handleAddNewCard = () => {
    const newCard = {
      id: `card-${Date.now()}`,
      title: '',
      content: '',
      delta: null
    };
    setCurrentCard(newCard);
    setIsNewCard(true);
    setIsModalOpen(true);
  };

  const handleSaveCard = (updatedCard) => {
    if (isNewCard) {
      setCards([...cards, updatedCard]);
    } else {
      setCards(cards.map(card => 
        card.id === updatedCard.id ? updatedCard : card
      ));
    }
    setIsModalOpen(false);
    saveAllData();
  };

  const handleDeleteCard = (cardId) => {
    setCards(cards.filter(card => card.id !== cardId));
    saveAllData();
  };

  const handleReload = () => {
    if (window.confirm('Are you sure you want to restore all cards to their original content? All your edits will be lost.')) {
      localStorage.removeItem('cardEditorData');
      localStorage.removeItem('cardEditorTimestamp');
      initializeDefaultContent();
    }
  };

  return (
    <div className="app">
      <Header 
        onSave={saveAllData} 
        onReload={handleReload} 
        onAddCard={handleAddNewCard} 
      />
      
      <main className="main-content">
        <div className="article-container">
          <h1 className="article-title">Getting Started with Card Editing</h1>
          
          {cards.map(card => (
            <Card 
              key={card.id}
              card={card}
              onEdit={handleEditCard}
              onDelete={handleDeleteCard}
            />
          ))}
        </div>
      </main>
      
      {isModalOpen && (
        <CardEditorModal
          card={currentCard}
          isNewCard={isNewCard}
          onSave={handleSaveCard}
          onClose={() => setIsModalOpen(false)}
        />
      )}
    </div>
  );
};

export default App;
