import React, { useState, useEffect, useRef, lazy, Suspense } from 'react';
import Card from './components/Card';
import ActionButtons from './components/ActionButtons';
import ArticleGenerator from './components/ArticleGenerator';
import { optimizedLocalStorage } from './utils/performance';

// Lazy load the CardEditor component
const CardEditor = lazy(() => import('./components/CardEditor'));

function App() {
  const [cards, setCards] = useState([]);
  const [articleTitle, setArticleTitle] = useState('');
  const [view, setView] = useState('loading'); // 'loading', 'generator', 'editor'
  const [showNotification, setShowNotification] = useState({ show: false, message: '', type: 'info' });
  const cardEditorRef = useRef();
  const [isEditorLoaded, setIsEditorLoaded] = useState(false);

  // Load saved data on component mount
  useEffect(() => {
    const savedData = optimizedLocalStorage.getItem('cardEditorData');
    const savedTitle = optimizedLocalStorage.getItem('articleTitle');

    if (savedData && Object.keys(savedData).length > 0) {
      try {
        const cardArray = Object.entries(savedData).map(([id, content]) => ({
          id,
          content
        }));
        setCards(cardArray);
        setArticleTitle(savedTitle || 'Untitled Article');
        setView('editor');
      } catch (error) {
        console.error('Error loading saved data:', error);
        setView('generator');
      }
    } else {
      setView('generator');
    }
  }, []);

  // Handle edit link clicks
  const handleEditCard = (card) => {
    // Dispatch custom event to open the editor
    const event = new CustomEvent('openEditModal', { detail: card });
    window.dispatchEvent(event);
  };

  const showNotificationMessage = (message, type = 'info') => {
    setShowNotification({ show: true, message, type });
    setTimeout(() => {
      setShowNotification({ show: false, message: '', type: 'info' });
    }, 4000);
  };

  const saveAllData = () => {
    const cardsData = {};
    cards.forEach(card => {
      cardsData[card.id] = card.content;
    });
    optimizedLocalStorage.setItem('cardEditorData', cardsData);
    optimizedLocalStorage.setItem('articleTitle', articleTitle);
    optimizedLocalStorage.setItem('cardEditorTimestamp', new Date().toISOString());
    showNotificationMessage('All edits saved locally!', 'success');
  };

  const reloadPage = () => {
    if (window.confirm('Are you sure you want to start over? All unsaved changes will be lost.')) {
      optimizedLocalStorage.removeItem('cardEditorData');
      optimizedLocalStorage.removeItem('articleTitle');
      window.location.reload();
    }
  };

  const addNewCard = () => {
    // Instead of directly adding a new card, open the editor for a new card
    const newCard = {
      id: 'card-' + Date.now(),
      content: ''
    };

    // Dispatch custom event to open the editor for a new card
    const event = new CustomEvent('openEditModal', { detail: newCard });
    window.dispatchEvent(event);
  };

  const handleGenerate = (topic, detailedPrompt) => {
    // Mock AI Generation
    const generatedTitle = topic.startsWith('How to') ? topic : `How to ${topic}`;
    setArticleTitle(generatedTitle);

    const mockCards = [
      {
        id: 'intro',
        content: `<h2 class="text-2xl font-semibold mb-4 text-gray-800">Introduction</h2>
          <p class="text-gray-600 mb-4">This guide will explain <strong>${topic}</strong> in detail.</p>
          <p class="text-gray-600 mb-4">${detailedPrompt ? `Based on your request: ${detailedPrompt}` : 'Follow these steps to achieve your goal.'}</p>
          <div class="flex justify-end"><a href="#" class="edit-link" data-card-id="intro">EDIT</a></div>`
      },
      {
        id: 'step1',
        content: `<h2 class="text-2xl font-semibold mb-4 text-gray-800">Step 1: Preparation</h2>
          <p class="text-gray-600 mb-4">First, gather all necessary resources and ensure you have a clear understanding of the requirements.</p>
          <div class="flex justify-end"><a href="#" class="edit-link" data-card-id="step1">EDIT</a></div>`
      },
      {
        id: 'step2',
        content: `<h2 class="text-2xl font-semibold mb-4 text-gray-800">Step 2: Execution</h2>
          <p class="text-gray-600 mb-4">Follow the core procedures carefully. Pay attention to details and verify each action.</p>
          <div class="flex justify-end"><a href="#" class="edit-link" data-card-id="step2">EDIT</a></div>`
      },
      {
        id: 'conclusion',
        content: `<h2 class="text-2xl font-semibold mb-4 text-gray-800">Conclusion</h2>
          <p class="text-gray-600 mb-4">By following these steps, you should successfully achieve your goal. Review the results and make adjustments as needed.</p>
          <div class="flex justify-end"><a href="#" class="edit-link" data-card-id="conclusion">EDIT</a></div>`
      }
    ];

    setCards(mockCards);
    setView('editor');

    // Save immediately so refresh keeps the article
    const cardsData = {};
    mockCards.forEach(card => {
      cardsData[card.id] = card.content;
    });
    optimizedLocalStorage.setItem('cardEditorData', cardsData);
    optimizedLocalStorage.setItem('articleTitle', generatedTitle);
  };

  // Callback function to open the edit modal
  const handleOpenEditModal = (card) => {
    if (cardEditorRef.current && cardEditorRef.current.openEditModal) {
      cardEditorRef.current.openEditModal(card);
    }
  };

  // Preload the editor when the app loads
  useEffect(() => {
    // Set a timeout to simulate loading
    const timer = setTimeout(() => {
      setIsEditorLoaded(true);
    }, 100);

    return () => clearTimeout(timer);
  }, []);

  if (view === 'loading') {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  if (view === 'generator') {
    return <ArticleGenerator onGenerate={handleGenerate} />;
  }

  return (
    <div className="bg-gray-100 flex justify-center py-10 px-4 sm:px-6 lg:px-8">
      <div className="container bg-white p-6 sm:p-8 rounded-lg shadow-lg">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900">{articleTitle}</h1>
          <button onClick={reloadPage} className="text-gray-500 hover:text-red-500 text-sm">Start Over</button>
        </div>

        {cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            onEdit={handleEditCard}
          />
        ))}

        <ActionButtons
          onSave={saveAllData}
          onAddCard={addNewCard}
          onClean={reloadPage}
        />
      </div>

      {/* Notification */}
      {showNotification.show && (
        <div className={`notification notification-${showNotification.type}`}>
          {showNotification.message}
        </div>
      )}

      {/* Edit Modal - Lazy loaded */}
      {isEditorLoaded && (
        <Suspense fallback={<div>Loading editor...</div>}>
          <CardEditor
            ref={cardEditorRef}
            cards={cards}
            setCards={setCards}
            showNotification={showNotificationMessage}
          />
        </Suspense>
      )}
    </div>
  );
}

export default App;
