            let parsedResponse;
            try {
                // Extract JSON if wrapped in code blocks
                const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
                if (jsonMatch) {
                    parsedResponse = JSON.parse(jsonMatch[0]);
                } else {
                    parsedResponse = JSON.parse(aiResponse);
                }
            } catch (e) {
                console.error('JSON Parse Error:', e);
                console.log('Raw Response:', aiResponse);
                throw new Error('Failed to parse AI response. Please try again.');
            }

            setArticleTitle(parsedResponse.title);

            const newCards = parsedResponse.sections.map(section => {
                // Convert markdown content to HTML
                let htmlContent = convertToHtml(section.content);

                // Prepend the title as an H2 if it's not already there (simple check)
                if (!htmlContent.includes(`<h2`)) {
                    htmlContent = `<h2 class="text-2xl font-semibold mb-4 text-gray-800">${section.title}</h2>` + htmlContent;
                }

                // Add edit link
                htmlContent += `<div class="flex justify-end"><a href="#" class="edit-link" data-card-id="${section.id}">EDIT</a></div>`;

                return {
                    id: section.id || `card-${Date.now()}-${Math.random()}`,
                    content: htmlContent
                };
            });

            setCards(newCards);

            // Save to ArticleManager
            ArticleManager.saveArticle(activeId, {
                title: parsedResponse.title,
                cards: newCards
            });

            setView('editor');
            showNotificationMessage('Article generated successfully!', 'success');

            // Clear refinement prompt after successful generation
            setRefinementPrompt('');
            // We keep filters active so user sees what was applied, or should we clear them?
            // User request says "Clear" button exists, so maybe keep them.

        } catch (error) {
            console.error('Generation Error:', error);
            showNotificationMessage(`Generation failed: ${error.message}`, 'error');
        } finally {
            setIsGenerating(false);
        }
    };

    // Filter Generation Logic
    const generateFilters = async () => {
        if (generatedFilters.length > 0) return; // Already generated

        setIsFiltersLoading(true);
        try {
            // Collect current content for context
            const contentContext = cards.map(c => c.content).join('\n').substring(0, 5000); // Limit context size

            const systemPrompt = `Analyze the following article content and suggest dynamic refinement filters.
            
            Article Title: "${articleTitle}"
            Content Snippet: ${contentContext}...
            
            Generate 3-5 relevant filter categories that would help a user refine or rewrite this article.
            For each category, provide options.
            
            Return JSON in this format:
            [
              {
                "id": "tone",
                "label": "Tone",
                "type": "segmented", // Use 'segmented' for 2-4 mutually exclusive options
                "options": [
                  { "label": "Formal", "value": "formal", "tooltip": "Professional and academic tone" },
                  { "label": "Casual", "value": "casual", "tooltip": "Friendly and relaxed tone" }
                ]
              },
              {
                "id": "audience",
                "label": "Target Audience",
                "type": "chip", // Use 'chip' for multiple choice or many options
                "options": [
                  { "label": "Beginner", "value": "beginner", "tooltip": "For absolute beginners" },
                  { "label": "Expert", "value": "expert", "tooltip": "For advanced users" }
                ]
              }
            ]
            
            Ensure the filters are relevant to the specific topic.`;

            const response = await fetch('https://api.perplexity.ai/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer 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'
                },
                body: JSON.stringify({
                    model: 'sonar-pro',
                    messages: [
                        { role: 'user', content: systemPrompt }
                    ],
                    temperature: 0.5,
                    max_tokens: 1000
                })
            });

            if (!response.ok) throw new Error('Failed to generate filters');

            const data = await response.json();
            const aiResponse = data.choices[0].message.content;

            let parsedFilters = [];
            try {
                const jsonMatch = aiResponse.match(/\[[\s\S]*\]/);
                if (jsonMatch) {
                    parsedFilters = JSON.parse(jsonMatch[0]);
                } else {
                    parsedFilters = JSON.parse(aiResponse);
                }
            } catch (e) {
                console.error('Filter Parse Error', e);
                // Fallback filters if parsing fails
                parsedFilters = [
                    {
                        id: 'tone',
                        label: 'Tone',
                        type: 'segmented',
                        options: [
                            { label: 'Professional', value: 'professional', tooltip: 'Standard business tone' },
                            { label: 'Friendly', value: 'friendly', tooltip: 'Warm and engaging' },
                            { label: 'Concise', value: 'concise', tooltip: 'To the point' }
                        ]
                    },
                    {
                        id: 'depth',
                        label: 'Detail Level',
                        type: 'chip',
                        options: [
                            { label: 'Overview', value: 'overview', tooltip: 'High level summary' },
                            { label: 'Step-by-Step', value: 'step-by-step', tooltip: 'Detailed instructions' },
                            { label: 'Technical', value: 'technical', tooltip: 'Deep technical details' }
                        ]
                    }
                ];
            }

            setGeneratedFilters(parsedFilters);

        } catch (error) {
            console.error('Filter Generation Error:', error);
            showNotificationMessage('Could not generate smart filters. Using defaults.', 'warning');
        } finally {
            setIsFiltersLoading(false);
        }
    };

    const handleRefine = () => {
        // Combine refinement prompt and active filters
        handleGenerate(articleTitle, refinementPrompt, currentArticleId, activeFilters);
    };

    const toggleFilterModal = () => {
        if (!isFilterModalOpen) {
            // Opening modal
            setIsFilterModalOpen(true);
            generateFilters();
        } else {
            setIsFilterModalOpen(false);
        }
    };

    const handleFilterChange = (sectionId, value, isMulti = false) => {
        setActiveFilters(prev => {
            const current = prev[sectionId];
            let newValue;

            if (isMulti) {
                const currentArray = Array.isArray(current) ? current : [];
                if (currentArray.includes(value)) {
                    newValue = currentArray.filter(v => v !== value);
                } else {
                    newValue = [...currentArray, value];
                }
            } else {
                newValue = value;
            }

            return { ...prev, [sectionId]: newValue };
        });
    };

    const clearFilters = () => {
        setActiveFilters({});
    };

    // Initialization & Routing
    useEffect(() => {
        const params = new URLSearchParams(window.location.search);
        const urlId = params.get('id');
        const urlTopic = params.get('topic');
        const urlTemplate = params.get('template');

        if (urlId) {
            // Case 1: Load existing article by ID
            const article = ArticleManager.getArticle(urlId);
            if (article) {
                setCurrentArticleId(urlId);
                setArticleTitle(article.title);
                setCards(article.cards || []);
                setView('editor');
            } else {
                console.error('Article not found');
                showNotificationMessage('Article not found. Redirecting to generator.', 'error');
                window.history.replaceState(null, '', '/');
                setView('generator');
            }
        } else if (urlTopic) {
            // Case 2: AI Link Trigger (New Article from Topic)
            // Create the article immediately
            const newId = ArticleManager.createArticle(urlTopic, urlTemplate || 'guide');
            setCurrentArticleId(newId);
            setArticleTitle(urlTopic);

            // Update URL so we don't regenerate on reload
            window.history.replaceState(null, '', `?id=${newId}`);

            // Trigger generation
            handleGenerate(urlTopic, null, newId);
        } else {
            // Case 3: No ID, No Topic. Check for legacy data migration or show generator.
            const migratedId = ArticleManager.migrateOldData();
            if (migratedId) {
                const article = ArticleManager.getArticle(migratedId);
                setCurrentArticleId(migratedId);
                setArticleTitle(article.title);
                setCards(article.cards || []);
                setView('editor');
                window.history.replaceState(null, '', `?id=${migratedId}`);
                showNotificationMessage('Restored your previous session.', 'info');
            } else {
                setView('generator');
            }
        }
    }, []);

    // Handle edit link clicks
    const handleEditCard = (card) => {
        const event = new CustomEvent('openEditModal', { detail: card });
        window.dispatchEvent(event);
    };

    const saveAllData = () => {
        if (!currentArticleId) {
            // Should not happen in editor view, but safety check
            const newId = ArticleManager.createArticle(articleTitle || 'Untitled');
            setCurrentArticleId(newId);
            window.history.pushState(null, '', `?id=${newId}`);
        }

        ArticleManager.saveArticle(currentArticleId, {
            title: articleTitle,
            cards: cards
        });

        showNotificationMessage('All edits saved to Library!', 'success');
    };

    const reloadPage = () => {
        if (window.confirm('Are you sure you want to start over? You will return to the generator.')) {
            window.location.href = '/';
        }
    };

    const addNewCard = () => {
        const newCard = {
            id: 'card-' + Date.now(),
            content: ''
        };
        const event = new CustomEvent('openEditModal', { detail: newCard });
        window.dispatchEvent(event);
    };

    // Callback function to open the edit modal
    const handleOpenEditModal = (card) => {
        if (cardEditorRef.current && cardEditorRef.current.openEditModal) {
            cardEditorRef.current.openEditModal(card);
        }
    };

    // Preload the editor when the app loads
    useEffect(() => {
        const timer = setTimeout(() => {
            setIsEditorLoaded(true);
        }, 100);
        return () => clearTimeout(timer);
    }, []);

    if (view === 'loading') {
        return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
    }

    if (view === 'generator') {
        return <ArticleGenerator onGenerate={handleGenerate} isGenerating={isGenerating} />;
    }

    return (
        <div className="bg-gray-100 flex justify-center py-10 px-4 sm:px-6 lg:px-8">
            <div className="container bg-white p-6 sm:p-8 rounded-lg shadow-lg">
                <div className="flex justify-end mb-2">
                    <button
                        onClick={reloadPage}
                        className="text-gray-400 hover:text-gray-600 focus:outline-none"
                        title="Start Over"
                    >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                        </svg>
                    </button>
                </div>
                <h1 className="text-4xl font-bold mb-8 text-gray-900">{articleTitle}</h1>

                {cards.map((card) => (
                    <Card
                        key={card.id}
                        card={card}
                        onEdit={handleEditCard}
                    />
                ))}

                <ActionButtons
                    onSave={saveAllData}
                    onAddCard={addNewCard}
                    onClean={reloadPage}
                />
            </div>

            {/* Refinement Bar */}
            <RefinementBar
                value={refinementPrompt}
                onChange={setRefinementPrompt}
                onFilterClick={toggleFilterModal}
                onSend={handleRefine}
                isGenerating={isGenerating}
                hasFilters={Object.keys(activeFilters).length > 0}
            />

            {/* Filter Modal */}
            <FilterModal
                isOpen={isFilterModalOpen}
                onClose={() => setIsFilterModalOpen(false)}
                filters={generatedFilters}
                selectedFilters={activeFilters}
                onFilterChange={handleFilterChange}
                onClean={clearFilters}
                onRegenerate={() => {
                    setIsFilterModalOpen(false);
                    handleRefine();
                }}
                isLoading={isFiltersLoading}
            />

            {/* Notification */}
            {showNotification.show && (
                <div className={`notification notification-${showNotification.type}`}>
                    {showNotification.message}
                </div>
            )}

            {/* Edit Modal - Lazy loaded */}
            {isEditorLoaded && (
                <Suspense fallback={<div>Loading editor...</div>}>
                    <CardEditor
                        ref={cardEditorRef}
                        cards={cards}
                        setCards={setCards}
                        showNotification={showNotificationMessage}
                    />
                </Suspense>
            )}
        </div>
    );
}

export default App;
