import React, { useState } from 'react';

const ArticleGenerator = ({ onGenerate }) => {
  const [topic, setTopic] = useState('');

  const handleGenerate = () => {
    if (topic.trim()) {
      onGenerate(topic, null);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleGenerate();
    }
  };

  return (
    <div className="bg-gray-100 flex justify-center py-10 px-4 sm:px-6 lg:px-8 min-h-screen">
      <div className="container bg-white p-6 sm:p-8 rounded-lg shadow-lg max-w-3xl w-full">
        <h1 className="text-4xl font-bold mb-8 text-gray-900">Article Generator</h1>

        <div className="mb-8 p-6 rounded-xl bg-white shadow-[0_4px_20px_rgba(0,0,0,0.08)] hover:shadow-[0_4px_25px_rgba(0,0,0,0.12)] transition-shadow duration-300">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">How To</h2>
          <div className="flex flex-col items-end">
            <input
              type="text"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Write what you need to create"
              className="w-full p-4 text-lg border-none bg-gray-50 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all placeholder-gray-400 mb-4"
            />
            <button
              onClick={handleGenerate}
              disabled={!topic.trim()}
              className="p-2 rounded-full bg-blue-500 text-white shadow-md hover:bg-blue-600 transition-all duration-300 flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed"
              title="Generate Article"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8"></path>
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleGenerator;
