import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfilePage = ({ onNavigate, onEditArticle }) => {
    const { user, logout } = useAuth();
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchArticles = async () => {
            if (!user) return;
            try {
                const response = await fetch(`http://localhost:3003/api/articles/user/${user.id}`, {
                    credentials: 'include', // Include cookies for authentication
                });
                if (!response.ok) throw new Error('Failed to fetch articles');
                const data = await response.json();
                setArticles(data.drafts || []);
            } catch (err) {
                console.error('Error fetching articles:', err);
                setError('Failed to load your articles.');
            } finally {
                setLoading(false);
            }
        };
        fetchArticles();
    }, [user]);

    const handleLogout = async () => {
        await logout();
        // Redirect to home page after logout
        navigate('/');
    };

    const handleDelete = async (articleId) => {
        try {
            const response = await fetch(`http://localhost:3003/api/articles/${articleId}`, {
                method: 'DELETE',
                credentials: 'include', // Include cookies for authentication
            });
            if (!response.ok) throw new Error('Failed to delete article');
            setArticles(articles.filter(article => article.id !== articleId));
        } catch (err) {
            console.error('Error deleting article:', err);
            alert('Failed to delete article');
        }
    };

    if (!user) {
        return (
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-800 mb-4">Access Denied</h2>
                    <p className="text-gray-600 mb-6">Please log in to view your profile.</p>
                    <button
                        onClick={() => onNavigate && onNavigate('home')}
                        className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
                    >
                        Go to Home
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-4xl mx-auto">
                <div className="bg-white rounded-lg shadow-md overflow-hidden">
                    <div className="px-6 py-8">
                        <div className="flex items-center justify-between mb-8">
                            <div>
                                <h1 className="text-3xl font-bold text-gray-900">My Profile</h1>
                                <p className="mt-2 text-gray-600">Welcome back, {user.email}</p>
                            </div>
                            <button
                                onClick={handleLogout}
                                className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded"
                            >
                                Logout
                            </button>
                        </div>

                        <div className="border-t border-gray-200 pt-8">
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">My Articles</h2>
                            
                            {loading ? (
                                <div className="text-center py-8">
                                    <p className="text-gray-600">Loading your articles...</p>
                                </div>
                            ) : error ? (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
                                    <p>{error}</p>
                                </div>
                            ) : articles.length === 0 ? (
                                <div className="text-center py-8">
                                    <p className="text-gray-600">You haven't created any articles yet.</p>
                                    <button
                                        onClick={() => onNavigate && onNavigate('home')}
                                        className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
                                    >
                                        Create Your First Article
                                    </button>
                                </div>
                            ) : (
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    {articles.map((article) => (
                                        <div key={article.id} className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow">
                                            <h3 className="text-xl font-bold text-gray-900 mb-2">{article.title || 'Untitled'}</h3>
                                            <p className="text-gray-600 text-sm mb-4">
                                                Created: {new Date(article.created_at).toLocaleDateString()}
                                            </p>
                                            <div className="flex space-x-3">
                                                <button
                                                    onClick={() => onEditArticle && onEditArticle(article.id)}
                                                    className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded text-sm"
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    onClick={() => handleDelete(article.id)}
                                                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded text-sm"
                                                >
                                                    Delete
                                                </button>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProfilePage;
