import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import Header from './Header';
import { useAuth } from '../context/AuthContext';

const CategoryArticlesPage = () => {
    const { category, subcategory } = useParams();
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const [articles, setArticles] = useState([]);
    const [categoryInfo, setCategoryInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCategoryArticles = async () => {
            try {
                setLoading(true);

                // 'sales-marketing' -> 'sales_marketing'
                const facetValue = subcategory.replace(/-/g, '_');

                const response = await fetch(
                    `http://localhost:3003/api/articles/categories/${facetValue}`
                );

                if (!response.ok) {
                    if (response.status === 404) {
                        setError('Category not found');
                    } else {
                        setError('Failed to fetch articles');
                    }
                    return;
                }

                const data = await response.json();
                setCategoryInfo(data.category);
                setArticles(data.articles || []);
            } catch (err) {
                console.error('Error fetching category articles:', err);
                setError('Failed to fetch articles');
            } finally {
                setLoading(false);
            }
        };

        fetchCategoryArticles();
    }, [category, subcategory]);

    const handleEditArticle = (article) => {
        console.log('üìù Opening article:', article.title);
        console.log('üìù Article content type:', typeof article.content);
        console.log('üìù Article content:', article.content);
        
        // Parse the content if it's a JSON string
        let articleContent = article.content;
        if (typeof articleContent === 'string') {
            try {
                articleContent = JSON.parse(articleContent);
                console.log('‚úÖ Parsed content:', articleContent);
            } catch (e) {
                console.error('‚ùå Failed to parse article content:', e);
                console.error('‚ùå Content was:', article.content);
                articleContent = [];
            }
        }

        const navigationState = {
            editArticle: {
                id: article.id,
                title: article.title,
                cards: articleContent
            }
        };
        
        console.log('üöÄ Navigating with state:', navigationState);
        
        // Navigate to home with article data in state
        navigate('/', {
            state: navigationState
        });
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                        <p className="mt-4 text-gray-600">Loading articles...</p>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="text-red-500 text-xl font-medium">{error}</div>
                        <button
                            onClick={() => window.location.reload()}
                            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                        >
                            Retry
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white">
            <Header user={user} onLogoutClick={logout} />
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">
                        {categoryInfo?.label || subcategory.replace(/-/g, ' ')}
                    </h1>
                    <p className="text-gray-600">
                        {categoryInfo?.facet?.label ? `${categoryInfo.facet.label}: ` : ''}
                        Articles in this category
                    </p>
                </div>

                {articles.length === 0 ? (
                    <div className="text-center py-12">
                        <p className="text-gray-500 mb-4">No articles found in this category.</p>
                        <p className="text-gray-400 text-sm">Check back later for new content.</p>
                    </div>
                ) : (
                    <div className="space-y-8">
                        {articles.map((article) => {
                            // Parse article content if it's a JSON string
                            let articleContent = article.content;
                            if (typeof articleContent === 'string') {
                                try {
                                    articleContent = JSON.parse(articleContent);
                                } catch (e) {
                                    console.error('Failed to parse article content:', e);
                                    articleContent = [];
                                }
                            }
                            
                            return (
                                <div key={article.id} className="bg-white shadow-md rounded-lg p-6 hover:shadow-lg transition-shadow">
                                    {/* Article Header */}
                                    <div className="mb-4 pb-4 border-b border-gray-200">
                                        <Link
                                            to={`/article/${subcategory}/${article.id}`}
                                            className="text-2xl font-bold text-blue-600 hover:underline block mb-2"
                                        >
                                            {article.title || 'Untitled Article'}
                                        </Link>
                                        <div className="flex items-center justify-between">
                                            <p className="text-sm text-gray-500">
                                                By {article.author?.name || article.author?.email?.split('@')[0] || 'Unknown Author'} ‚Ä¢ {article.updated_at ? new Date(article.updated_at).toLocaleDateString() : ''}
                                            </p>
                                            <div className="flex flex-wrap gap-1">
                                                {article.facetAssignments?.slice(0, 3).map((assignment) => (
                                                    <span
                                                        key={assignment.id}
                                                        className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-blue-50 text-blue-700"
                                                    >
                                                        {assignment.value?.label}
                                                    </span>
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                    
                                    {/* Article Cards/Sections */}
                                    {Array.isArray(articleContent) && articleContent.length > 0 ? (
                                        <div className="space-y-6">
                                            {articleContent.map((card, index) => (
                                                <div key={index} className="">
                                                    {card.heading && (
                                                        <h2 className="text-xl font-bold text-gray-900 mb-3">{card.heading}</h2>
                                                    )}
                                                    <div className="prose max-w-none text-gray-700">
                                                        <p className="whitespace-pre-wrap">{card.content}</p>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    ) : (
                                        <p className="text-gray-500 italic">No content available</p>
                                    )}
                                    
                                    {/* Edit Button */}
                                    <div className="mt-6 pt-4 border-t border-gray-200">
                                        <button
                                            onClick={() => handleEditArticle(article)}
                                            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                                        >
                                            Open in Editor
                                        </button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        </div>
    );
};

export default CategoryArticlesPage;
