import React from 'react';
import { Link } from 'react-router-dom';

const BlogCard = ({ article }) => {
    return (
        <div className="card-large">
            <Link to={`/blog/${article.slug}`}>
                <h2>{article.title}</h2>
            </Link>

            <span className="--gray">by {article.author} | {article.date}</span>

            <p>
                <img
                    style={{ width: "100%", float: "left", marginRight: "10px", paddingBottom: "10px" }}
                    src={article.image}
                    alt={article.title}
                />
                {article.excerpt}
            </p>
        </div>
    );
};

export default BlogCard;
