import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import Header from './Header';
import CardEditor from './CardEditor';
import { useAuth } from '../context/AuthContext';

const CategoryArticlesPage = () => {
    const { category, subcategory } = useParams();
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const [articles, setArticles] = useState([]);
    const [categoryInfo, setCategoryInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [expandedArticles, setExpandedArticles] = useState({});
    const [notification, setNotification] = useState({ show: false, message: '', type: 'success' });

    useEffect(() => {
        const fetchCategoryArticles = async () => {
            try {
                setLoading(true);

                // 'sales-marketing' -> 'sales_marketing'
                const facetValue = subcategory.replace(/-/g, '_');

                const response = await fetch(
                    `http://localhost:3003/api/articles/categories/${facetValue}`
                );

                if (!response.ok) {
                    if (response.status === 404) {
                        setError('Category not found');
                    } else {
                        setError('Failed to fetch articles');
                    }
                    return;
                }

                const data = await response.json();
                setCategoryInfo(data.category);
                setArticles(data.articles || []);
            } catch (err) {
                console.error('Error fetching category articles:', err);
                setError('Failed to fetch articles');
            } finally {
                setLoading(false);
            }
        };

        fetchCategoryArticles();
    }, [category, subcategory]);

    const toggleArticleExpand = (articleId) => {
        setExpandedArticles(prev => ({
            ...prev,
            [articleId]: !prev[articleId]
        }));
    };

    const parseArticleContent = (article) => {
        let articleContent = article.content;
        if (typeof articleContent === 'string') {
            try {
                articleContent = JSON.parse(articleContent);
            } catch (e) {
                console.error('Failed to parse article content:', e);
                articleContent = [];
            }
        }
        return Array.isArray(articleContent) ? articleContent : [];
    };

    const handleSaveArticle = async (articleId, updatedCards) => {
        if (!user) {
            showNotification('Please login to save articles', 'error');
            return;
        }

        try {
            const response = await fetch('http://localhost:3003/api/articles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify({
                    title: articles.find(a => a.id === articleId)?.title || 'Untitled',
                    content: JSON.stringify(updatedCards),
                    status: 'draft'
                })
            });

            if (response.ok) {
                showNotification('Article saved to your profile!', 'success');
            } else {
                showNotification('Failed to save article', 'error');
            }
        } catch (error) {
            console.error('Error saving article:', error);
            showNotification('Error saving article', 'error');
        }
    };

    const showNotification = (message, type = 'success') => {
        setNotification({ show: true, message, type });
        setTimeout(() => {
            setNotification({ show: false, message: '', type: 'success' });
        }, 3000);
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                        <p className="mt-4 text-gray-600">Loading articles...</p>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-white">
                <Header user={user} onLogoutClick={logout} />
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                    <div className="text-center py-12">
                        <div className="text-red-500 text-xl font-medium">{error}</div>
                        <button
                            onClick={() => window.location.reload()}
                            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                        >
                            Retry
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white">
            <Header user={user} onLogoutClick={logout} />
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">
                        {categoryInfo?.label || subcategory.replace(/-/g, ' ')}
                    </h1>
                    <p className="text-gray-600">
                        {categoryInfo?.facet?.label ? `${categoryInfo.facet.label}: ` : ''}
                        Articles in this category
                    </p>
                </div>

                {articles.length === 0 ? (
                    <div className="text-center py-12">
                        <p className="text-gray-500 mb-4">No articles found in this category.</p>
                        <p className="text-gray-400 text-sm">Check back later for new content.</p>
                    </div>
                ) : (
                    <div className="space-y-6">
                        {articles.map((article) => {
                            const isExpanded = expandedArticles[article.id];
                            const articleCards = parseArticleContent(article);
                            
                            return (
                                <div key={article.id} className="bg-white shadow rounded-lg overflow-hidden">
                                    {/* Article Header */}
                                    <div className="p-6 border-b border-gray-200">
                                        <div className="flex items-start justify-between">
                                            <div className="flex-1">
                                                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                                                    {article.title || 'Untitled Article'}
                                                </h2>
                                                <p className="text-sm text-gray-500 mb-3">
                                                    By {article.author?.name || article.author?.email?.split('@')[0] || 'Unknown Author'} â€¢ {article.updated_at ? new Date(article.updated_at).toLocaleDateString() : ''}
                                                </p>
                                                <div className="flex flex-wrap gap-2">
                                                    {article.facetAssignments?.slice(0, 5).map((assignment) => (
                                                        <span
                                                            key={assignment.id}
                                                            className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                                                        >
                                                            {assignment.value?.label}
                                                        </span>
                                                    ))}
                                                </div>
                                            </div>
                                            <button
                                                onClick={() => toggleArticleExpand(article.id)}
                                                className="ml-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors flex-shrink-0"
                                            >
                                                {isExpanded ? 'Collapse' : 'Read & Edit'}
                                            </button>
                                        </div>
                                    </div>

                                    {/* Article Content - Editable Cards */}
                                    {isExpanded && (
                                        <div className="p-6 bg-gray-50">
                                            {articleCards.length > 0 ? (
                                                <CardEditor
                                                    cards={articleCards}
                                                    setCards={(updatedCards) => {
                                                        // Update local state
                                                        setArticles(prev => prev.map(a => 
                                                            a.id === article.id 
                                                                ? { ...a, content: JSON.stringify(updatedCards) }
                                                                : a
                                                        ));
                                                    }}
                                                    showNotification={showNotification}
                                                />
                                            ) : (
                                                <p className="text-gray-500 text-center py-8">No content available</p>
                                            )}
                                            
                                            {user && articleCards.length > 0 && (
                                                <div className="mt-6 flex justify-end">
                                                    <button
                                                        onClick={() => handleSaveArticle(article.id, articleCards)}
                                                        className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
                                                    >
                                                        Save to My Profile
                                                    </button>
                                                </div>
                                            )}
                                        </div>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                )}

                {/* Notification */}
                {notification.show && (
                    <div className="fixed bottom-4 right-4 z-50">
                        <div className={`px-6 py-3 rounded-lg shadow-lg ${
                            notification.type === 'success' ? 'bg-green-500' : 'bg-red-500'
                        } text-white`}>
                            {notification.message}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default CategoryArticlesPage;
