import React from 'react';
import RichTextDisplay from './RichTextDisplay';

const Card = ({ card, onEdit }) => {
    // Handle edit link and ai-link clicks within the card
    const handleEditClick = (e) => {
        // Handle edit links
        if (e.target.classList.contains('edit-link')) {
            e.preventDefault();
            onEdit(card);
            return;
        }

        // Handle AI-links
        if (e.target.tagName.toLowerCase() === 'ai-link') {
            e.preventDefault();
            const topic = e.target.getAttribute('topic');
            const template = e.target.getAttribute('template') || 'guide';

            if (topic) {
                // Open in new tab with topic parameter
                const url = `/?topic=${encodeURIComponent(topic)}&template=${template}`;
                window.open(url, '_blank');
            }
        }
    };

    return (
        <div
            className="mb-8"
            data-card-id={card.id}
            onClick={handleEditClick}
        >
            <RichTextDisplay content={card.content} />
        </div>
    );
};

export default React.memo(Card);
