import React from 'react';
import { useParams, Link } from 'react-router-dom';
import blogData from '../data/blog.json';
import SEO from '../utils/seo';

const BlogPost = () => {
    const { slug } = useParams();
    const article = blogData.articles.find(a => a.slug === slug);

    if (!article) {
        return <div className="p-8 text-center">Article not found. <Link to="/blog" className="text-blue-500">Back to Blog</Link></div>;
    }

    return (
        <div className="container mx-auto p-6 max-w-4xl">
            <SEO
                title={article.title}
                description={article.excerpt}
                ogTitle={article.title}
                ogDescription={article.excerpt}
                ogImage={article.image}
            />

            <Link to="/blog" className="text-blue-500 mb-4 inline-block">&larr; Back to Blog</Link>

            <article className="prose lg:prose-xl">
                <h1 className="text-4xl font-bold mb-4">{article.title}</h1>
                <div className="text-gray-600 mb-8">
                    <span>{article.author}</span> | <span>{article.date}</span>
                </div>

                <img
                    src={article.image}
                    alt={article.title}
                    className="w-full h-auto mb-8 rounded-lg shadow-md"
                />

                <div className="text-lg leading-relaxed">
                    {/* 
            Note: In a real app, the full content would be here. 
            For now, we are using the excerpt or placeholder content 
            since the JSON only has excerpts.
          */}
                    <p className="font-semibold text-xl mb-4">{article.excerpt}</p>

                    <p>
                        (Full article content would go here. Currently using placeholder text as the provided data only contained excerpts.)
                    </p>
                    <p>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                    </p>
                </div>
            </article>
        </div>
    );
};

export default BlogPost;
