import React, { useEffect } from 'react';
import blogData from '../data/blog.json';
import SEO from '../utils/seo';
import BlogIntro from './BlogIntro';
import BlogSidebar from './BlogSidebar';
import BlogCard from './BlogCard';

const BlogList = () => {
    return (
        <div className="container-grid-brief">
            <SEO
                title={blogData.title}
                description={blogData.metaDescription}
                keywords={blogData.metaKeywords}
                canonicalUrl={blogData.canonicalUrl}
                ogTitle={blogData.ogTitle}
                ogDescription={blogData.ogDescription}
            />

            <BlogIntro />

            <div className="brief">
                <article>
                    <h1>{blogData.title}</h1>
                    <hr className="brief--hr" />

                    <p>Welcome to the <b> Work and Success Stories</b> blog on the <b>www.briefer.pro</b> website!</p>

                    <p>Our blog is an exciting journey into the world of work, where we explore interesting stories and learn from practical cases presented by various companies and experts. Here, we take a deep breath and dive into stories of success, dissect challenging situations, and share lessons that can be applied to your own endeavors.</p>

                    <p><b>What you will find with us:</b></p>

                    <ul>
                        <li><b>Inspirational Cases:</b> We'll tell you about companies that have achieved great results in the digital realm. Discover how they overcame challenges and created innovations.</li>
                        <li><b>Expert Insights:</b> Our authors and invited experts share their knowledge and experience in the field of work. You'll learn about the latest trends and best practices.</li>
                        <li><b>Lessons from Mistakes:</b> Sometimes, even failures can be a source of valuable lessons. We'll tell you about situations when things didn't go as planned and how to extract lessons for successful projects.</li>
                        <li><b>Technologies and Innovations:</b> The world of work is constantly evolving, and we stay up-to-date with the latest events. Learn about new technologies, tools, and methods that can help you achieve better results.</li>
                    </ul>

                    <p>We invite you to join us on our journey into the world of digital projects, where each story and case is an opportunity to learn, grow, and get inspired. Join our <b> Work Stories</b> community and be at the forefront of innovation!</p>

                    <p>Stay tuned for our publications and subscribe to ensure you don't miss any exciting stories.</p>

                    <br /><br />

                    <section>
                        {blogData.articles.map((article, index) => (
                            <BlogCard key={index} article={article} />
                        ))}
                    </section>
                </article>

                <BlogSidebar />
            </div>

            <div className="side">
                <div className="side__content">
                    {/* Placeholder for side.php content if needed, or just empty as per current plan */}
                </div>
            </div>
        </div>
    );
};

export default BlogList;
